create table doctor (id bigint generated by default as identity, created_on timestamp, creator varchar(70), recorded boolean default true, updated_on timestamp, updater varchar(70), version varchar(10), name varchar(70) not null, primary key (id));
create table medical_prescription (id bigint generated by default as identity, created_on timestamp, creator varchar(70), recorded boolean default true, updated_on timestamp, updater varchar(70), version varchar(10), done_at timestamp, registered_at timestamp, treatment_id bigint, primary key (id));
create table medical_prescription_detail (id bigint generated by default as identity, created_on timestamp, creator varchar(70), recorded boolean default true, updated_on timestamp, updater varchar(70), version varchar(10), amount integer, medicine_id bigint, prescription_id bigint, primary key (id));
create table medicine (id bigint generated by default as identity, created_on timestamp, creator varchar(70), recorded boolean default true, updated_on timestamp, updater varchar(70), version varchar(10), name varchar(70) not null, process_duration integer, primary key (id));
create table patient (id bigint generated by default as identity, created_on timestamp, creator varchar(70), recorded boolean default true, updated_on timestamp, updater varchar(70), version varchar(10), name varchar(70) not null, primary key (id));
create table treatment (id bigint generated by default as identity, created_on timestamp, creator varchar(70), recorded boolean default true, updated_on timestamp, updater varchar(70), version varchar(10), called boolean not null, number integer, registration_date date, registration_time timestamp, doctor_id bigint, medical_prescription_id bigint, patient_id bigint, primary key (id));
